insert into ability (id, ability_type, name, description)
--- BASIC abilities
values
('b0ddfc6d-67ad-4a33-b4b8-1664bfb87a37', 'BASIC', 'Здоровье', 'Количество урона, которое вы можете принять на себя, 1=5 ед. Хп. При хп=0 персонаж теряет сознание, любой последующий урон приводит к смерти.'),
('d017b327-bb4a-4e16-ba9a-7d6cc0f02f1a', 'BASIC', 'Защита', 'Насколько ваше тело способно выдерживать урон, защита от брони суммируется.'),
('b51c0edf-041b-44d9-81df-57c709616f9b', 'BASIC', 'Атака', 'Физический урон, который вы способны нанести противнику.'),
('a63fe46a-53ed-4d9d-9c84-3599c004da64', 'BASIC', 'Маг. атака', 'Влияет на разрушительную силу атаки в бою, силу контроля, прочность барьеров и т.д.'),
('a5e9c494-9218-4f56-ab80-a594eed7b835', 'BASIC', 'Маг. защита', 'Защита от магических воздействий или враждебных зелий.'),
('45d6b331-2679-41e0-9410-6bbbe498fb29', 'BASIC', 'Точность', 'Насколько метко вы попадаете заклинанием, кулаком или любым метательным средством в цель. А так же это определяет ваш успех попадания по противнику вне боя.'),
('a9c78db7-cc95-4c2e-8e46-13be3115f008', 'BASIC', 'Уклонение', 'Вероятность удачного уворота от атаки, заклинания или не боевого взаимодействия. Может быть только меньше или равна точности'),
('c3a2d634-cd35-4e30-a17d-1b95e0159855', 'BASIC', 'Время', 'Время действия продолжительных заклинаний, призванных существ, объектов или приготовленных зелий, а также количество ходов в бою, которое демон, нефилим или персонаж с навыком «левитация» может находиться в воздухе 1=1 пост \ цикл ходов (точность, уклонение, атака, защита)'),
('162f2b23-636b-4b25-9ca1-8b884f4a5a85', 'BASIC', 'Сила', 'Возможность поднимать тот или иной вес, эффективность в толкании предметов, также данный стат важен для совершения резких рывков, отвечает за то, насколько сильно вы можете сжимать объекты или удерживать их на месте. 1=20 кг.)
Поднятие тяжести зависит от веса персонажа, т.е. вы можете поднять половину собственного веса, суммируя баллы Силы к нему. Пример: персонаж весит 60 кг, он может поднять 30 кг, но если у него есть Сила 1, то 30+20 = 50 кг поднятого веса.
Использование веса при расчете силы действия подразумевает некий упор, рычаг. Без опоры воспользоваться весом проблематично.'),
('3dc02121-b270-454d-8d83-f7cbddc67ae9', 'BASIC', 'Скорость', 'Максимальная скорость, по которой вы можете разогнаться во время бега или полета, 1=1 км/ч. Этот навык привязан к вашему росту и имеет зависимость от него, к примеру персонаж в высоту 2 метра, его начальная скорость = 20 км в час при беге. Во время полета скорость умножается на 2. Так же стоит понимать, что скорость стат+значение от роста это скорость перемещения на большие расстояния, скорость же в бою или при побеге берется только из стата.
Пример: Рост 2 метра — 20 км/ч скорость от роста. Стат = 3. От сюда скорость побега 3км/ч. Скорость бега из города в город. 20+3 = 23км/ч. Отсюда полет на короткое расстояние это 3х2=6км/ч. Перелет же из города в город 23х2 = 46км/ХПр'),
--- FIGHTING abilities
('130388a6-264d-4dda-a5e1-d8efad1c7463', 'FIGHTING', 'Рукопашный бой', ''),
('f2c3daf3-81c5-4ed7-afd6-6692ecc2e21c', 'FIGHTING', 'Владение кинжалом', 'Знание приема Амбидекстрия позволяет использовать сразу два оружия в бою и с вероятностью !монетка ребро наносить урон обоими.'),
('8e0af80e-b271-43fa-9347-c22e8007ddc1', 'FIGHTING', 'Одноручный меч и топор', 'Одноручное оружие позволяет иметь одновременно два оружия в каждой руке, но удар за ход, за исключением особых приемов, можно нанести только одним из них. Знание приема Амбидекстрия позволяет использовать сразу два оружия в бою и с вероятностью !монетка ребро наносить урон обоими.'),
('b6d95417-0d76-4723-9ba3-cc3b3ea7ab1a', 'FIGHTING', 'Одноручное Дробящее оружие', 'Одноручное оружие позволяет иметь одновременно два оружия в каждой руке, но удар за ход, за исключением особых приемов, можно нанести только одним из них. Знание приема Амбидекстрия позволяет использовать сразу два оружия в бою и с вероятностью !монетка ребро наносить урон обоими.'),
('0fce82c5-afde-4db0-94a7-33cd0c8507fb', 'FIGHTING', 'Двуручный меч и секира', ''),
('e555825d-618f-43ab-9dd5-7493e5cb401e', 'FIGHTING', 'Специальное оружие (цепи, хлысты, плеть)', ''),
('6d45b312-9c23-4a42-ab0b-6a9eb3aa1688', 'FIGHTING', 'Древковое оружие (посохи, копья, глефы)', ''),
('4684b6c4-bc37-489c-8e3a-9246a8faa4cb', 'FIGHTING', 'Метательное оружие (Луки и Арбалеты)', 'Важно! 1 флакон яда = 1 выпущенной стреле и 1 яд действует раз за бой (обязательно). Приемы дальнего боя действуют только на расстоянии. Удерживаемый или окруженный плотным кольцом врага использовать их не не может. От противника необходимо будет отдалиться затратив ход на перемещение.'),
('8a7b01b1-072c-4b2a-8fcc-4fb8097f9085', 'FIGHTING', 'Мастерство щита', '*При нанесении урона щитом, вместо атаки оружия используется Защита щита в формуле'),
('f36b5819-c139-42c4-8613-9367df9a1d93', 'FIGHTING', 'Мастерство брони', '*При нанесении урона броней, вместо атаки оружия используется Защита брони в формуле. Персонаж может одеть на себя броню, для ношения которой требуется навык выше имеющегося, но в этом случае он не может использовать ее прибавки и приемы от навыка ношения, но все минусы, что накладывает тяжелая броня остаются. Броня навык 5, -2 скорость, -1 уворот(7 слитков + Средний маг.кристалл или выше) стат Защита и МЗащита из материала х3. Для ношения брони требуется уровень 5. Если навык есть, защита брони работает, если навыка нет, броня тебя не защитит, но все минусы на скорость и уворот останутся'),
--- GENERAL abilities
('c3e5f42e-bf55-4d1d-8977-88d6e7e6273e', 'GENERAL', 'Образованность', 'Запрет на артефакты. То, насколько хорошо ваш персонаж владеет письмом, чтением, языком жестов или умением говорить на разных языках.'),
('de5e4a11-5ecf-4eb0-b3ee-ef9b9cf6d08e', 'GENERAL', 'Верховая езда', 'То, насколько хорошо ваш персонаж может ездить в седле. Если вы избрали ездовым животным оборотня, то только при согласии другого игрок и уровень владения навыком должен быть не меньше 3.'),
('e4180a09-71a4-4c2e-a7e2-61f79c8c5ae0', 'GENERAL', 'Плавание', 'Навык необходим для некоторых квестов и конкурсов, чем выше навык, тем лучше персонаж чувствует себя в воде.'),
('a83d25f6-f743-4de5-9f3d-d1cda78f5a0a', 'GENERAL', 'Воровство', 'Позволяет незаметно забрать ту или иную вещь. Как работает: вы подходите к цели, прокидывается Куб на осторожную кражу. Ваш опонент использует навык Обнаружение. При провале, вас замечают с поличным.'),
('b5c0d7b3-8c4c-46f8-9726-6a2a23e98f57', 'GENERAL', 'Взлом', 'Позволяет взламывать и открывать замки. При взломе замка используются инструменты: отмычка или нож. Простые не магические замки можно сломать навыком Сила, но он используется без прибавок веса, это доступно с 1-5 уровень замка.'),
('dc9d6382-5e2e-4c82-97e3-c1d8b6d4f7c2', 'GENERAL', 'Обнаружение', 'Позволяет определять скрытых и маскирующихся существ, а так же ловушки или подмешанные зелья. Для того, чтобы использовать этот навык, в беседе боя вы кидаете команду !дайс+уровень обнаружения.'),
('1ec4539e-5d7b-496b-bde5-19458edb7d38', 'GENERAL', 'Скрытность', 'Способность прятаться, бесшумно передвигаться и делать все, что хотелось бы утаить персонажу. Для того, чтобы использовать этот навык, в беседе боя вы кидаете команду !дайс+уровень скрытности.'),
('b724c7d0-7f76-4cf2-bf83-3e00a3d0d4eb', 'GENERAL', 'Следопыт', 'Помогает разбираться в следах и находить животных или существ, которые вам нужны, по оставленному следу. Очень полезны для оборотня, что бы учуять свою цель. Для поиска любых следов бросается !дайс+уровень навыка. Чен нашел. Не чет не нашел. Для продвижения по обнаруженному следу бросается !дайс+уровень навыка против скрытности цели. При проигрыше след утерян.'),
('b3e3b1ff-174d-44f8-bb44-feb93bfb4886', 'GENERAL', 'Политика', 'Навык необходим правителям и полководцам. Чем выше навык, тем вернее стража и слуги, сложнее их подкупить или устроить переворот. Очень важный аспект, который влияет на большинство игровых механик.'),
('c3eb2aaf-2b24-48b4-a50a-6f636acfb798', 'GENERAL', 'Красноречие', 'Способность лгать, убеждать, запугивать. Распознать ложь или игру способен лишь более талантливый игрок.'),
('8b28fd7e-dc79-44e3-92ed-bc5408e7c76f', 'GENERAL', 'Музыка', 'Этот навык нужен, чтобы играть на музыкальных инструментах и знать ноты.'),
('26d8e87d-09d8-4d6b-9639-b3dc1cfdc3b6', 'GENERAL', 'Пение', 'Умение воспроизвести ту или иную песню, балладу, колыбельную.'),
('6d549b65-7d91-4a4e-bf43-8c1b11c57064', 'GENERAL', 'Рисование', 'Этот навык просто необходим творцам и художникам, а так же тем, кто создает схемы, чертежи и зарисовки. Без него невозможно нанести грим или макияж.'),
('7f5f41c2-600a-4b70-b0f4-dae8d1d6d759', 'GENERAL', 'Танцы', 'Делать изящные пируэты или плясать под ритм. Только существам с этим талантом доступно двигаться под музыку красиво.'),
('a19d2e78-4c5c-46e6-8a5f-74d6b38b8dc4', 'GENERAL', 'Регенерация', 'Пассивный навык, помогает восстановить магическую и жизненную энергию, а так же отвечает за внутреннюю способность организма к самоисцелению. Действует только на вашего персонажа. Можно использовать 1 раз в реальный день!'),
('b5d79f4b-9f8b-44a4-b31f-06b2a7a0a1ae', 'GENERAL', 'Медицина', 'Нужна для исцеления игроков и может отыгрываться как магическим воздействием, так и привычным наложением подорожника, зеленки или бинта. На 1 персонаже или нпс можно использовать 1 раз в реальный день!'),
('f4cfd7a7-d832-4baf-bb6e-728a1a689f6d', 'GENERAL', 'Инженерия', 'Навык, необходимый для изобретателей, ученых. строителей и механиков, весьма удобен и многосторонен.'),
('d227f51a-621c-4e93-a63c-8efb3e6e4b62', 'GENERAL', 'Морское дело', 'Навык необходим для создания флота и путешествий по морям.'),
('c3d5c0e6-342a-40cc-8b65-94cf1b4b46a5', 'GENERAL', 'Животноводство', 'Навык, необходимый для открытия ферм и работы с различной живностью. Так же он нужен для тех, кто приручает себе питомца и в последствии может этого зверя развивать. Чем выше уровень зверя, тем сложнее его приручить.'),
('2b7f5a3d-dc63-4b8f-baad-339a83555f3b', 'GENERAL', 'Рукоделие', 'Навык необходим для некоторых квестов, поиска работы и конкурсов. Благодаря ему вы способны обучиться вышивке, кройке вещей, созданию тканевой обуви (для кожаных ботинок требуется Кожевничество).'),
('4eaa6a9a-15a7-4a2f-a9b7-c2c708d8d64f', 'GENERAL', 'Кулинария', 'Благодаря этому навыку вы способны что-либо приготовить и насытиться. Вкусно по домашнему и с любовью.'),
('b58e9f7a-26a1-4423-9354-9286e82b80b8', 'GENERAL', 'Алхимия', 'Это мистическая наука, цель которой — постижение сути всех существующих в мире вещей и расширение привычных рамок бытия!'),
('d6ff493a-5e37-4427-89bc-4e30dbb9e8ab', 'GENERAL', 'Травничество', 'Навык, который позволяет вашему персонажу найти особые, лечебные, ядовитые или магические травы и тем самым воспользоваться их свойствами в своих нуждах.'),
('d4d1e9af-5a8d-493d-b7b7-4c8498d6787d', 'GENERAL', 'Артефакторика', 'Позволяет создавать зачарованные кольца, ожерелья и другие побрякушки с прибавкой к характеристикам, но их невозможно передать собственным персонажам, а вероятность продажи другим игрокам только с 10 уровня и лишь знакомым по сюжету.'),
('1ecb4dc3-54b1-4e69-83c8-40b5c68f30b2', 'GENERAL', 'Охота', 'Навык, позволяющий выслеживать зверей и читая их следы находить, а после успешного поиска, действовать на своё усмотрение. Так же этот навык позволяет ловить рыбу в подходящей локации. Поиск животного проводится при помощи куба !охота+ур.охоты'),
('2d342905-4a2c-4f26-9e2a-69dc1a5d69b6', 'GENERAL', 'Кожевничество', 'Этот навык позволяет создавать вещи из кожи, в том числе и броню, а также орудия для защиты или же красоты.Весьма полезное умение, если вы не любите тяжелую броню. Кожаное обмундирование мобильно и позволяет не терять скорости в маневрах, однако по защите оно имеет средний уровень.'),
('0fc38db5-86c3-4e1a-815e-9298f458be07', 'GENERAL', 'Кузнечное дело', 'Кузнец — это почетная профессия, она невероятно сложна, но и благородна. Позволяет создавать оружие и броню, однако её невозможно передать собственным персонажам, а вероятность продажи другим игрокам только по рп.');

